userDefinedLinkHintCss:
div > .vimiumHintMarker {
	background: rgba(44, 46, 52, 0.9); 
	border: 2px solid #dbb774;  
	padding: 2px 6px;
}

div > .vimiumHintMarker span {
	color: #b6bec9;
	font-weight: bold;
	font-size: 12px;
}

#vomnibar {
	box-shadow: none;
	background: none;
	border: 1px solid #2c2e34;
	margin: 0;
	padding: 0;
	border-radius: 0;
	box-shadow: 0 4px 5px #000000a1;
	border-radius: 4px; 
}

#vomnibar .vomnibarSearchArea {
	background: rgba(44, 46, 52, 0.9);
	padding: 5px;
	border-radius: 0;
	border: 0;
}

#vomnibar ul {
	padding: 0;
	margin: 0;
	background: rgba(44, 46, 52, 0.9); 
}

#vomnibar li {
	padding: 5px 10px;
	font-size: 12px;
	background: none; 
	border-bottom: 1px dashed #dbb774;
}

#vomnibar li.vomnibarSelected {
	background-color: #5b6371; 
}

#vomnibar li .vomnibarMatch, 
#vomnibar li .vomnibarTitle .vomnibarMatch {
	color: #dbb774; 
}

#vomnibar li .vomnibarSource, 
#vomnibar li .vomnibarTitle {
	color: #b6bec9; 
}

#vomnibar li .vomnibarUrl {
	color: #5ebaa5; 
}

#vomnibar input {
	border: 1px solid #dbb774;
	background: none; 
	border-radius: 4px;
	font-size: 12px;
	height: auto;
	font-weight: bold;
	color: #b6bec9;
}
